function scrollGallery(e,t){const n=document.getElementById("gallery-"+e),s=320;n.scrollBy({left:t*s,behavior:"smooth"})}document.addEventListener("DOMContentLoaded",function(){const e=document.querySelectorAll(".gallery-container");e.forEach(e=>{let t=0,n=0;e.addEventListener("touchstart",s=>{t=s.touches[0].pageX-e.offsetLeft,n=e.scrollLeft}),e.addEventListener("touchmove",s=>{s.preventDefault();const o=s.touches[0].pageX-e.offsetLeft,i=(o-t)*2;e.scrollLeft=n-i})})})